add_filter( 'aioseo_schema_output', function ( $schema ) {
    foreach ( $schema as &$schemaItem ) {
        if ( isset( $schemaItem['@type'] ) && 'Product' === $schemaItem['@type'] ) {
			if(!empty($schemaItem["offers"]["shippingDetails"])){
				// Loop inside the Offers > shippingDetails schema
				foreach( $schemaItem["offers"]["shippingDetails"] as &$schemaItemChild ){
				// Check for OfferShippingDetails schema where we need to add the deliveryTime schema
				if( isset( $schemaItemChild['@type'] ) && 'OfferShippingDetails' === $schemaItemChild['@type'] ){
					// Adding the deliveryTime schema
					$schemaItemChild["deliveryTime"] = [
						"@type" => "ShippingDeliveryTime",
						"handlingTime" => [
							"@type" => "QuantitativeValue",
							"minValue" => 1,
							"maxValue" => 2,
							"unitCode" => "d"
						],
						"transitTime" => [
							"@type" => "QuantitativeValue",
							"minValue" => 1,
							"maxValue" => 3,
							"unitCode" => "d"
						]
					];
				}
			}		
			}	
        }
    }

    return $schema;
}, 10, 2 );

add_filter( 'aioseo_sitemap_images', 'aioseo_filter_sitemap_images', 10, 2 );
function aioseo_filter_sitemap_images( $images, $post ) {
	if ( ! function_exists( 'get_field' ) ) {
		return $images;
	}

	$customImage = get_field( 'custom_image', $post->ID );
	if ( ! empty( $customImage ) ) {
		$images[] = wp_get_attachment_image_url( $customImage['id'] );
	}

	return $images;
}

add_filter( 'aioseo_schema_output', function ( $schema ) {
	foreach ( $schema as &$schemaItem ) {
		if (
			empty( $schemaItem['mainEntity'] ) &&
			isset( $schemaItem['@type'] ) &&
			'ProfilePage' === $schemaItem['@type']
		) {
			global $wp;

			$current_url              = untrailingslashit( home_url( $wp->request ) );
			$schemaItem['mainEntity'] = [
				'@id' => $current_url . '/#author'
			];
		}
	}

	return $schema;
} );

