add_filter( 'aioseo_schema_output', 'aioseo_filter_schema_output' );

function aioseo_filter_schema_output( $schema ) {
	foreach ( $schema as $index => $graphData ) {
		if ( empty( $graphData['@type'] ) ) {
			continue;
		}

		$type = strtolower( $graphData['@type'] );
		switch ( $type ) {
			case 'article':
			case 'blogposting':
			case 'newsarticle':
				unset( $schema[ $index ]['datePublished'] );
				unset( $schema[ $index ]['dateModified'] );
				break;
			default:
				break;
		}
	}

	return $schema;
}

add_action( 'admin_menu', 'aioseo_hide_search_stats_menu', 99999 );

function aioseo_hide_search_stats_menu() {
	global $submenu;
	if ( ! isset( $submenu['index.php'] ) ) {
		return;
	}

	foreach ( $submenu['index.php'] as $index => $props ) {
		if ( ! empty( $props[2] ) && admin_url( '/admin.php?page=aioseo-search-statistics' ) === $props[2] ) {
			unset( $submenu['index.php'][ $index ] );
			return;
		}
	}
}

